mapper.select:


     and t.task_id = #{taskId}
     and tt.year = #{taskYear}
     and t.assessed_user_id = #{assessedUserId}
     and t.vote_user_id = #{voteUserId}
     and t.status = #{status}
     and t.overall_evaluation = #{overallEvaluation}
     and t.de_evaluation = #{deEvaluation}
     and t.neng_evaluation = #{nengEvaluation}
     and t.qin_evaluation = #{qinEvaluation}
     and t.ji_evaluation = #{jiEvaluation}
     and t.lian_evaluation = #{lianEvaluation}
     and tcu.name like concat('%', #{taskCreateByName}, '%')
     and au.name like concat('%', #{assessedUserName}, '%')
     and vu.name like concat('%', #{voteUserName}, '%')

order by t.create_time desc

