mapper.select:
SELECT  b.id, b.name, description, b.create_by, b.create_time, b.update_by, b.update_time  FROM exam_question_bank as b   JOIN
(SELECT r.question_bank_id  FROM exam_question_bank_role as r


            and r.role_id in

                #{roleId}



) as a on b.id = a.question_bank_id

mapper.update:
update exam_question_bank
name = #{name}, description = #{description}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime},
where id = #{id}
