select.where:
and t.year = #{year}
and u.nick_name like concat('%',#{userName},'%')
and t.dept_name like concat('%',#{deptName},'%')
and t.confirm_status = #{confirmStatus}
and t.confirm_time = #{confirmTime}
and t.user_id = #{userId}

${params.dataScope}

mapper.update:
update biz_probity
year = #{year}, user_id = #{userId}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime}, remark = #{remark}, confirm_status = #{confirmStatus}, confirm_time = #{confirmTime},
where id = #{id}

