mapper.select:

        where d.del_flag = '0'

			AND (d.parent_id = #{parentId} OR d.dept_id = #{parentId})


			AND d.dept_name like concat('%', #{deptName}, '%')


			AND d.dept_code like concat('%', #{deptCode}, '%')


			AND d.status = #{status}


			AND d.leader_charge = #{leaderCharge}


		${params.dataScope}
		order by d.parent_id, d.order_num

select d.dept_id from sys_dept d left join sys_role_dept rd on d.dept_id = rd.dept_id where rd.role_id = #{roleId}
and d.dept_id not in (select d.parent_id from sys_dept d inner join sys_role_dept rd on d.dept_id = rd.dept_id and rd.role_id = #{roleId})
order by d.parent_id, d.order_num

mapper.update:
update sys_dept set ancestors =

    when #{item.deptId} then #{item.ancestors}

where dept_id in

    #{item.deptId}

mapper.select:
select * from sys_dept
where del_flag = '0' and status = '0'
	and dept_id=#{deptId}

select * from sys_dept d where d.del_flag = '0'




				and d.dept_id > #{historyId}

			and d.dept_id <= #{id}


			and d.dept_id > #{id}




	and TO_TIMESTAMP(d.update_time, 'YYYY-MM-DD HH24:MI:SS') >= TO_TIMESTAMP(#{lastExportTime}, 'YYYY-MM-DD HH24:MI:SS')

order by d.dept_id asc

	, #{orderBy}


	limit #{limit}
